import React, { useState } from 'react';
import './SignIn.css'
import { FormField, Button, Checkbox, Form } from 'semantic-ui-react'
import {
     Radio, Segment, Label
  } from 'semantic-ui-react'

// Instructor Signup Form
const SigninPage = () => {
    

//Backend TODO
const handleSignin = async () => {

};

  return (

         <Form>
    <FormField className="email">
      <label>Email</label>
      <input type="text" placeholder="Email" className="textfield" />
    </FormField>
    <FormField className="password">
      <label>Password</label>
      <input type="text" placeholder="Password" className="textfield" />
    </FormField>
    <FormField>
      <Checkbox label='I agree to the Terms and Conditions' />
    </FormField>
    <Button type='submit'>Submit</Button>
  </Form>
        
   
  );

  
};

const SignInPage = () => {
    const [isInstructor, setIsInstructor] = useState(true);
  
    const toggleSignup = () => {
      setIsInstructor(!isInstructor);
    };
  
    return (
      <div style={{
        display: 'grid',
        placeItems: 'center',
        backgroundColor: '#f0f0f0'
      }}>
        <h1>{isInstructor ? 'Instructor' : 'Student'} Signup Page</h1>
        <Segment compact style={{
          backgroundColor: '#f0f0f0',
          border: 'none',
        }}>
          <Label size='big' style={{
            transform: 'translateY(-5px)'
          }} basic>Student</Label>
          <Radio toggle checked={isInstructor} onChange={toggleSignup} /><Label size='big' style={{
            transform: 'translateY(-5px)'
          }} basic>Instructor</Label>
        </Segment>
        <div>
          {isInstructor ? <InstructorSignup /> : <StudentSignup />}
        </div>
      </div>
    );
  };



export default SignInPage;
