import React, { useState } from 'react';
import axios from 'axios';
import './SignUp.css'

// Instructor Signup Form
const InstructorSignup = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [section, setSection] = useState('');

  const handleSignup = async () => {
    const instructor = { name, email, password, section };
    try {
      await axios.post('http://localhost:8080/api/instructors/signup', instructor);
      alert('Instructor signed up successfully!');
    } catch (error) {
      console.error(error);
      alert('Failed to sign up.');
    }
  };

  return (
    <div>
  


      <h2>Instructor Signup</h2>
      <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <input type="text" placeholder="Section" value={section} onChange={(e) => setSection(e.target.value)} />
      <button onClick={handleSignup}>Sign Up</button>
    </div>
  );
};

// Student Signup Form
const StudentSignup = () => {
  const [studentId, setStudentId] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [section, setSection] = useState('');

  const handleSignup = async () => {
    const student = { studentId, name, email, password, section };
    try {
      // Send POST request to sign up student
      const response = await axios.post('http://localhost:8080/api/students/signup', student);
      
      // Check if the signup was successful (assuming the backend sends success message in response)
      if (response.data === true) {
        alert('Student signed up successfully!');
      } else {
        alert('Student section does not exist!');
      }
    } catch (error) {
      console.error(error);
      alert('Failed to sign up.');
    }
  };
  
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);
  const togglePasswordVisibility = () => {
  setIsPasswordVisible(!isPasswordVisible); 
};
  return (
    <div className="App">
    <header className="App-header">
      <table className="Table">
      
        <thead>
        <h3 className="title">Sign Page Students</h3>
        </thead>

        <tbody className="content">

          <table className="table2">
            <tr>
              <th colSpan="2">
          
              <p>
              First Name: &nbsp;&nbsp;<input type="text" className="firstname"></input>
              </p>
        
              </th>
            

              <th> 
              <p>
              Last Name: <input type="text"  className="lastname"></input>
              </p>
              </th>
            </tr>
        
            <tr>
              <th colSpan="2">
        
                <p>
            
                  Email: 
                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <input type="text" className="Email"></input></p>
              </th>
        
              <th>
                <p>Section number: <input type="text" className="Section"></input></p>
              </th>

              </tr>
              <tr>
              <th>
                <p>Password: <input type={isPasswordVisible ? "text" : "password"} className="password"></input>
                
                
                </p>
                

              </th>
              </tr>
        <br></br><br></br><br></br><br></br><br></br><br></br>
        { <div className="login-container"><button className="login-name">Login</button></div> }

        </table>
        </tbody>

        <br></br><br></br><br></br><br></br><br></br>
        <br></br><br></br><br></br><br></br><br></br><br></br>
        <br></br><br></br><br></br><br></br><br></br>
        
       
      
      </table>
    </header>
  </div>
    
      
      
    
  );
};

// Main Signup Page with Toggle Button
const SignupPage = () => {
  const [isInstructor, setIsInstructor] = useState(true);

  const toggleSignup = () => {
    setIsInstructor(!isInstructor);
  };

  return (
    <div>
      <h1>{isInstructor ? 'Instructor' : 'Student'} Signup Page</h1>
      <button onClick={toggleSignup}>
        Switch to {isInstructor ? 'Student' : 'Instructor'} Signup
      </button>
      <div>
        {isInstructor ? <InstructorSignup /> : <StudentSignup />}
      </div>
    </div>
  );
};

export default SignupPage;
